mapper.select:
select od.id as orderId, od.created_time as createdTime, od.pay_method as payMethod, od.real_pay_amount as realPayAmount, od.post_amount as postAmount, os.order_status as orderStatus, od.is_comment as isComment from orders od left join order_status os on od.id = os.order_id where od.user_id = #{paramsMap.userId} and od.is_delete = 0
and os.order_status = #{paramsMap.orderStatus}
order by od.updated_time ASC

select od.id as orderId, od.created_time as createdTime, od.pay_method as payMethod, od.real_pay_amount as realPayAmount, od.post_amount as postAmount, os.order_status as orderStatus, od.is_comment as isComment, oi.item_id as itemId, oi.item_name as itemName, oi.item_img as itemImg, oi.item_spec_id as itemSpecId, oi.item_spec_name as itemSpecName, oi.buy_counts as buyCounts, oi.price as price from orders od left join order_status os on od.id = os.order_id left join order_items oi on od.id = oi.order_id where od.user_id = #{paramsMap.userId} and od.is_delete = 0
and os.order_status = #{paramsMap.orderStatus}
order by od.updated_time ASC
